mapper.select:
SELECT id,name,faculty FROM t_specialized
id = #{id}
and faculty = #{faculty}
and name like '%' #{name} '%'

SELECT id,name,specialized,clazzYear
FROM
t_clazz


        id = #{id}


        and name like '%' #{name} '%'


        and specialized = #{specialized}


        and clazzYear = #{clazzYear}

SELECT DISTINCT c.id,c.name
from t_course c,
     t_course_clazz cc

    cc.course = c.id

        and c.id = #{course}


        and cc.clazz in
        ${item}


        and c.teacher = #{teacher}


        and c.courseYear = #{courseYear}


        and c.term = #{term}

select id,name
FROM t_teacher


        faculty = #{faculty}


        and profes = #{profes}


        and sex = #{sex}

select year,name from t_year


        year = #{year}


        and name = #{name}


order by year asc
